// Function to calculate the system's cut from each bet
function calculateSystemCut(betAmount) {
    return Math.ceil(betAmount * 0.1); // 10% cut
}

// Function to reset the game and update scores
function resetGame() {
    mathScore = 0;
    wordamentScore = 0;
    betAmount = 0;
    document.getElementById('math-score').textContent = mathScore;
    document.getElementById('wordament-score').textContent = wordamentScore;
    document.getElementById('bet-amount').textContent = betAmount;
}

// Function to end the game and display final results
function endGame() {
    const totalCoinsWon = mathScore * 10 + wordamentScore;
    const systemCut = calculateSystemCut(betAmount);
    const playerCoinsWon = totalCoinsWon - systemCut;
    alert(`Game over!\nTotal coins won: ${playerCoinsWon}\nSystem cut: ${systemCut}`);
    resetGame();
}

// Check if both games have been played
function checkGameOver() {
    if (mathScore > 0 && wordamentScore > 0) {
        endGame();
    }
}

// Add event listener to input fields to check for Enter key press
document.addEventListener('DOMContentLoaded', function() {
    document.getElementById('word').addEventListener('keypress', function(event) {
        if (event.key === 'Enter') {
            submitWord();
        }
    });

    document.getElementById('word').addEventListener('keypress', function(event) {
        if (event.key === 'Enter') {
            submitAnswer();
        }
    });
});